import React, { Component } from "react";
import Form from "./Form";
import CardContainer from "./CardContainer";
import ShowDetails from "./ShowDetails";
import "./App.scss";

export default class App extends Component {

  state = {
    foxes: [],
    foxItem: {}
  }

  handleShowClick = () => {
    console.log("hey")
  }

  componentDidMount(){
    fetch("http://localhost:3000/foxes")
    .then(res => res.json())
    .then(foxObj => {
      this.setState({
        foxes: foxObj
      })
    })
  }

  render() {
    return (
      <React.Fragment>
        <Form />
        <CardContainer
          foxes={this.state.foxes} handleShowClick={this.state.handleShowClick}
        />
        <ShowDetails />
      </React.Fragment>
    );
  }
}
